import React, { useState, useEffect } from "react";
import DayCell from "./daycell";

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date(2024, 0, 1));
  const [holidays, setHolidays] = useState([]);

  useEffect(() => {
    const fetchHolidays = async () => {
      const month = currentDate.getMonth() + 1;
      const year = currentDate.getFullYear();
      try {
        const response = await fetch(`http://localhost:8089/api/holidays?year=${year}&month=${month}`);
        const data = await response.json();
        setHolidays(data);
      } catch (error) {
        console.error("Error fetching holidays:", error);
      }
    };
    fetchHolidays();
  }, [currentDate]);

  const prevMonth = () => {
    const prev = new Date(currentDate);
    prev.setMonth(prev.getMonth() - 1);
    setCurrentDate(new Date(prev));
  };

  const nextMonth = () => {
    const next = new Date(currentDate);
    next.setMonth(next.getMonth() + 1);
    setCurrentDate(new Date(next));
  };

  const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
  const firstDay = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1).getDay(); // 0 = Sunday

  let dayCells = [];

  // ðŸ”¹ Add empty placeholders before the first day of the month
  for (let i = 0; i < firstDay; i++) {
    dayCells.push(<div key={`empty-${i}`} className="p-4 bg-gray-100"></div>);
  }

  // ðŸ”¹ Add actual days of the month
  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = `${currentDate.getFullYear()}-${currentDate.getMonth() + 1}-${day}`;
    const holidaysForDay = holidays.filter((h) => h.date === dateStr);

    dayCells.push(
      <div key={dateStr} className="p-2 border rounded-lg bg-white flex items-center justify-center">
        <DayCell date={dateStr} holidays={holidaysForDay} refreshHolidays={() => setCurrentDate(new Date(currentDate))} />
      </div>
    );
  }

  return (
    <div className="p-4 max-w-2xl mx-auto bg-gray-50 shadow-lg rounded-lg">
      <div className="flex justify-between items-center mb-4">
        <button onClick={prevMonth} className="bg-blue-500 text-white px-3 py-1 rounded">Prev</button>
        <h2 className="text-xl font-bold">
          {currentDate.toLocaleString("default", { month: "long" })} {currentDate.getFullYear()}
        </h2>
        <button onClick={nextMonth} className="bg-blue-500 text-white px-3 py-1 rounded">Next</button>
      </div>

      {/* Weekday Labels */}
      <div className="grid grid-cols-7 gap-2 text-center font-bold text-gray-700 mb-2">
        <div>Sun</div>
        <div>Mon</div>
        <div>Tue</div>
        <div>Wed</div>
        <div>Thu</div>
        <div>Fri</div>
        <div>Sat</div>
      </div>

      {/* Calendar Grid */}
      <div className="grid grid-cols-7 gap-2 bg-white p-4 shadow-md rounded-lg">
        {dayCells}
      </div>
    </div>
  );
};

export default Calendar;
