import React, { useState, useEffect } from "react";
import DayCell from "./daycell";
import "../styles/calendar.css";

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date(2024, 0, 1));
  const [holidays, setHolidays] = useState([]);
  const [selectedYear, setSelectedYear] = useState(2024); // Year dropdown

  const fetchHolidays = async () => {
    const month = currentDate.getMonth() + 1;
    try {
      const response = await fetch(`http://localhost:8089/api/holidays?year=${selectedYear}&month=${month}`);
      const data = await response.json();
      setHolidays(data);
    } catch (error) {
      console.error("Error fetching holidays:", error);
    }
  };

  useEffect(() => {
    fetchHolidays(); // Auto-fetch when the component loads or year changes
  }, [currentDate, selectedYear]);

  const prevMonth = () => {
    const prev = new Date(currentDate);
    prev.setMonth(prev.getMonth() - 1);
    if (prev.getFullYear() < 2024) return;
    setCurrentDate(prev);
  };

  const nextMonth = () => {
    const next = new Date(currentDate);
    next.setMonth(next.getMonth() + 1);
    if (next.getFullYear() > 2029) return;
    setCurrentDate(next);
  };

  const handleYearChange = (event) => {
    setSelectedYear(Number(event.target.value));
  };

  const daysInMonth = new Date(selectedYear, currentDate.getMonth() + 1, 0).getDate();
  const dayCells = [];

  for (let day = 1; day <= daysInMonth; day++) {
    const dateStr = `${selectedYear}-${currentDate.getMonth() + 1}-${day}`;
    const holidaysForDay = holidays.filter((h) => h.date === dateStr);
    dayCells.push(
      <DayCell key={dateStr} date={dateStr} holidays={holidaysForDay} refreshHolidays={fetchHolidays} />
    );
  }

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button onClick={prevMonth} className="nav-button">Prev</button>
        
        <h2>{currentDate.toLocaleString("default", { month: "long" })}</h2>

        <select value={selectedYear} onChange={handleYearChange} className="year-dropdown">
          {Array.from({ length: 6 }, (_, i) => 2024 + i).map((year) => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>

        <button onClick={nextMonth} className="nav-button">Next</button>
      </div>

      {/* Fetch Holidays Button */}
      <div className="fetch-holidays-container">
        <button onClick={fetchHolidays} className="fetch-holidays-button">
          Fetch Holidays
        </button>
      </div>

      <div className="calendar-grid">{dayCells}</div>
    </div>
  );
};

export default Calendar;
