import React from "react";
import "../styles/.css";

const DayCell = ({ date, holidays, refreshHolidays }) => {
  const addHoliday = async () => {
    try {
      const response = await fetch("http://localhost:8089/api/holidays", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ date, name: "New Holiday" }),
      });
      if (response.ok) {
        refreshHolidays();
      }
    } catch (error) {
      console.error("Error adding holiday:", error);
    }
  };

  const deleteHoliday = async (id) => {
    try {
      const response = await fetch(`http://localhost:8089/api/holidays/${id}`, {
        method: "DELETE",
      });
      if (response.ok) {
        refreshHolidays();
      }
    } catch (error) {
      console.error("Error deleting holiday:", error);
    }
  };

  return (
    <div className="day-cell">
      <span>{date.split("-")[2]}</span>
      {holidays.map((holiday) => (
        <div key={holiday.id} className="holiday-item">
          {holiday.name}
          <button className="delete-button" onClick={() => deleteHoliday(holiday.id)}>X</button>
        </div>
      ))}
      <button className="add-button" onClick={addHoliday}>+</button>
    </div>
  );
};

export default DayCell;
