import React, { useState } from "react";
import "../styles/daycell.css";


const DayCell = ({ date, holidays, refreshHolidays }) => {
  const [showAdd, setShowAdd] = useState(false);
  const [holidayName, setHolidayName] = useState("");
  const [holidayDesc, setHolidayDesc] = useState("");

  // Function to add a holiday
  const addHoliday = async () => {
    const payload = {
      date,
      name: holidayName,
      description: holidayDesc,
    };
    try {
      const response = await fetch("http://localhost:8089/api/holidays", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (response.ok) {
        // Clear inputs and refresh holidays
        setHolidayName("");
        setHolidayDesc("");
        refreshHolidays();
      } else {
        console.error("Error adding holiday, status:", response.status);
        const errText = await response.text();
        console.error("Server response:", errText);
      }
    } catch (error) {
      console.error("Error adding holiday:", error);
    }
  };

  // Function to delete a holiday
  const deleteHoliday = async (holidayId) => {
    try {
      const response = await fetch(`http://localhost:8089/api/holidays/${holidayId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        refreshHolidays();
      } else {
        console.error("Error deleting holiday, status:", response.status);
      }
    } catch (error) {
      console.error("Error deleting holiday:", error);
    }
  };

  return (
    <div
      className="border p-2 relative rounded-lg bg-white shadow-sm hover:bg-gray-50 transition"
      onMouseEnter={() => setShowAdd(true)}
      onMouseLeave={() => setShowAdd(false)}
    >
      <div className="text-sm font-semibold text-gray-700 text-center">{date.split("-")[2]}</div>

      {/* Display existing holidays with a delete button */}
      {holidays.map((holiday) => (
        <div key={holiday.id} className="flex items-center justify-between bg-red-100 p-1 rounded-md mt-1 text-xs">
          <span className="text-red-700">{holiday.name}</span>
          <button
            onClick={() => deleteHoliday(holiday.id)}
            className="bg-red-500 text-white px-2 py-0.5 rounded text-xs hover:bg-red-600 transition"
          >
            ðŸ—‘
          </button>
        </div>
      ))}

      {/* Show the add holiday form on hover */}
      {showAdd && (
        <div className="absolute top-0 left-0 right-0 bg-white border p-2 rounded-md shadow-lg z-10">
          <input
            type="text"
            placeholder="Holiday Name"
            value={holidayName}
            onChange={(e) => setHolidayName(e.target.value)}
            className="w-full border p-1 text-xs rounded-md focus:outline-none focus:ring focus:ring-blue-200 mb-1"
          />
          <input
            type="text"
            placeholder="Description"
            value={holidayDesc}
            onChange={(e) => setHolidayDesc(e.target.value)}
            className="w-full border p-1 text-xs rounded-md focus:outline-none focus:ring focus:ring-blue-200 mb-1"
          />
          <button
            onClick={addHoliday}
            className="w-full bg-green-500 text-white py-1 text-xs rounded-md hover:bg-green-600 transition"
          >
            Add Holiday
          </button>
        </div>
      )}
    </div>
  );
};

export default DayCell;
