import React, { useState } from "react";
import "../styles/daycell.css";

const DayCell = ({ date, holidays, refreshHolidays }) => {
  const [showAdd, setShowAdd] = useState(false);
  const [holidayName, setHolidayName] = useState("");
  const [holidayDesc, setHolidayDesc] = useState("");
  const [loading, setLoading] = useState(false); // Prevents duplicate requests

  const addHoliday = async () => {
    if (!holidayName.trim()) {
      alert("Please enter a holiday name.");
      return;
    }

    setLoading(true); // Disable button while processing
    const payload = { date, name: holidayName, description: holidayDesc };

    try {
      const response = await fetch("http://localhost:8080/api/holidays", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        setHolidayName("");
        setHolidayDesc("");
        refreshHolidays(); // Refresh holidays after successful addition
      } else {
        console.error("Error adding holiday:", response.statusText);
      }
    } catch (error) {
      console.error("Error adding holiday:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="day-cell"
      onMouseEnter={() => setShowAdd(true)}
      onMouseLeave={() => setShowAdd(false)}
    >
      <div className="day-number">{date.split("-")[2]}</div>
      {holidays.map((holiday, idx) => (
        <div key={idx} className="holiday-name">{holiday.name}</div>
      ))}
      {showAdd && (
        <div className="add-holiday-form">
          <input
            type="text"
            placeholder="Holiday Name"
            value={holidayName}
            onChange={(e) => setHolidayName(e.target.value)}
            className="holiday-input"
          />
          <input
            type="text"
            placeholder="Description"
            value={holidayDesc}
            onChange={(e) => setHolidayDesc(e.target.value)}
            className="holiday-input"
          />
          <button onClick={addHoliday} className="add-button" disabled={loading}>
            {loading ? "Adding..." : "Add"}
          </button>
        </div>
      )}
    </div>
  );
};

export default DayCell;
